function scrollHeader(){const o=$(".main-header");let e=0;$(window).scroll(()=>{const s=$(window).scrollTop();s>100&&s>e?o.addClass("out"):o.removeClass("out"),e=s})}function getMenu(){const o=$(".main-nav__list"),e=$(".main-nav__mobile-burger");e.click(()=>{o.toggleClass("open"),e.toggleClass("cross")})}function getActiveLink(){const o=$(".main-nav__link");o.click(function(){o.removeClass("active"),$(this).addClass("active"),$(".main-nav__list").removeClass("open"),$(".main-nav__mobile-burger").removeClass("cross"),$(".main-header").addClass("out")})}function togglePopup(){const o=$(".popup"),e=$(".overlay");$(".work__item").click(()=>{o.addClass("open"),e.addClass("open")}),$(".overlay").click(()=>{o.removeClass("open"),e.removeClass("open")})}function getMoreWorks(){const o=$(".work__button"),e=$(".work__inner");o.click(()=>{e.toggleClass("all-works"),e.hasClass("all-works")?o.text("it is all work"):o.text("load more work")})}function playVideo(){const o=$("#process__video"),e=$(".process__play-button");e.click(()=>{o.attr("controls",!0),$(o).get(0).play(),e.hide()}),o.on("pause",()=>{e.show(),o.attr("controls",!1)})}$(document).ready(()=>{$(".owl-carousel").owlCarousel({loop:!0,smartSpeed:500,items:1,nav:!1,dots:!0}),scrollHeader(),getMenu(),getActiveLink(),togglePopup(),getMoreWorks(),playVideo()});